docker_app_dir=/root/docker-appsfunction copy_modules() {  cd $docker_app_dir/tmp  npm install  cd -  rsync -a $docker_app_dir/tmp/node_modules $docker_app_dir/3-record  cd -}function deploy() {  if [ ! "$(docker ps -q -f name=record)" ]; then      if [ "$(docker ps -aq -f status=exited -f name=record)" ]; then          # cleanup          echo "===container already exists==="          docker restart record      fi      # run your container      echo "===running znki==="      docker run \        -d --name record -p 3000:3000 \        -e NODE_ENV=production \        -v /root/docker-apps/3-record:/app node \        bash -c "cd /app;node main.js"  fi}copy_modules;deploy;